import UIKit

class RecipeListInteractor: RecipeListInteractorInputProtocol {
    var remoteDataManager: RecipeRemoteDataManagerInputProtocol?
    weak var presenter: RecipeListInteractorOutputProtocol?
    
    //MARK:- Fetch list of recipe from server
    func fetchRecipeListFromServer() {
        self.remoteDataManager?.retrieveRecipeList()
    }
}

extension RecipeListInteractor: RecipeRemoteDataManagerOutputProtocol {
    func onRecieveRecipe(item: [RecipeModel]) {
        presenter?.onRecieveRecipe(items: item)
    }

    func onRecieve(error: APIError) {
        presenter?.onRecieveServer(error: error)
    }
}
